mapper.select:
select
l.* ,p.*
from t_asrs_log_devalarm l
left join t_web_project p on l.projectid = p.id


    and devno = #{devno,jdbcType=VARCHAR}


    and acode = #{acode,jdbcType=VARCHAR}


   and    and DATE_FORMAT(ltime , '%Y-%m-%d')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d')   



    and #{endTime,jdbcType=TIMESTAMP}>=ltime


    and l.companyid = #{companyId,jdbcType=VARCHAR}
