mapper.select:
select

from t_web_zuhu

   1=1

    and id=#{id,jdbcType=INTEGER}


    and company=#{company,jdbcType=VARCHAR}


    and tel=#{tel,jdbcType=VARCHAR}


   and postcode=#{postcode,jdbcType=VARCHAR}


    and pre_company=#{preCompany,jdbcType=VARCHAR}


    and ismain=#{ismain,jdbcType=INTEGER}


    and address=#{address,jdbcType=VARCHAR}


    and caegory=#{caegory,jdbcType=VARCHAR}


    and maxnum_user=#{maxnumUser,jdbcType=BIGINT}


    and maxnum_company=#{maxnumCompany,jdbcType=INTEGER}


    and maxnum_dev=#{maxnumDev,jdbcType=BIGINT}


    and service_address=#{serviceAddress,jdbcType=VARCHAR}


    and create_time=#{createTime,jdbcType=TIMESTAMP}
